<script>
	import {addQueryParamsToUrl, currentRoute, navigate} from 'pluma-svelte-router';

	function addParams () {
		addQueryParamsToUrl({
			name: 'Pepito',
			food: 'tacos'
		});
	}

	function openModal () {
		navigate('/about/some-modal');
	}

</script>

<h1>About</h1>
<p>Watch the browser's address when opening the modal!</p>

<div class="mt-4 mb-4">
	<button type="button" class="btn btn-primary" on:click={openModal}>Open modal</button>
</div>

<h3>Query params</h3>
<p>Add query params to the URL with this button, and then either refresh or navigate back and forth.</p>
<div class="mt-4 mb-4">
